import { setAttribute } from '@timecat/virtual-dom'
import { delay } from '@timecat/utils'
import { ContainerComponent } from './container'
import { Component, html, IComponent } from '../utils'

@Component(
    'player-pointer',
    html`<div class="player-pointer">
        <img
            src="data:image/png;base64,"
            alt="pointer"
        />
        <div class="spinner"></div>
    </div>`
)
export class PointerComponent implements IComponent {
    parent: HTMLElement
    target: HTMLElement

    x = 0
    y = 0

    show: boolean
    pointer: HTMLElement
    c: ContainerComponent
    constructor(c: ContainerComponent) {
        this.c = c
        this.initPointer()
    }

    initPointer() {
        this.pointer = this.c.container.querySelector('.player-pointer') as HTMLElement
        this.move(0, 0)
    }

    showPointer(show = true) {
        this.show = show
        ;(this.pointer.firstElementChild as HTMLElement).style.display = show ? 'inherit' : 'none'
    }

    move(x: number, y: number) {
        this.x = x
        this.y = y
        this.pointer.style.left = this.x + 'px'
        this.pointer.style.top = this.y + 'px'
    }

    async click(x: number, y: number) {
        this.move(x, y)
        if (this.pointer.hasAttribute('active')) {
            return
        }
        await delay(200)
        setAttribute(this.pointer, 'active', '')
        await delay(400)
        setAttribute(this.pointer, 'active', null)
    }
}
